import React, { useEffect } from 'react'
import Data from './colleges.json'
import Image from "./images/college_02.jpg"


const Card=() =>{
    const [state,setState]= useState({
        list: [],
      pageNo: 1,
      displayCount: 5,
      noMoreData: false,
    })
  
    useEffect(()=> {
        fetchData();
      })


      useEffect(()=> {
        container.addEventListener('scroll', () => {
          const CONTAINER_HEIGHT = container.getBoundingClientRect().height;
          const { top: bottomLineOffsetTop } = bottomLine.getBoundingClientRect();
    
          if (bottomLineOffsetTop <= CONTAINER_HEIGHT) {
            console.log('load more data');
            fetchData();
          }
        });
      })

      
  const fetchData=()=> {
    const { list, pageNo, displayCount, noMoreData } = state;

    if (noMoreData) {
      console.log('no more data');
      return;
    }

    if (pageNo > 6) {
      // no more data
      setState({
        noMoreData: true
      });
    } else {
      let responseList = [];

      for(let i = 0; i < 5; i++) {
        responseList.push({
          name: `from-page-${pageNo}`
        });
      }
      setState({
        list: [ ...list, ...responseList ],
        pageNo: pageNo + 1
      });
    }
  }

    

    return (
      <div className="App">
       
          { Data.colleges.map((post) => {

            return (
              <>
             
               <div className="card-container">
                   <div className="image-container">
                       <img src={Image} alt="img"/>
                       <h2 className="heading">{post.tags}</h2>
                       <div style={{display:"flex"}}>
                       <div style={{marginLeft:"15px",fontWeight:"bold",color:"gray"}}>{post.college_name}</div> 
                       <span style={{marginLeft:"200px",fontSize:"11px",color:"gray" }}><s>&#8377;{post.original_fees}</s>  
                        <span style={{ marginLeft:"10px",backgroundColor:"red",color:"white"}}>{post.discount}</span></span>
                       </div>
                       <div style={{display:"flex",marginTop:"10px"}}>
                        <span style={{ marginLeft:"15px",fontSize:"12px"}}>{post.nearest_place} | <span style={{color:"gray"}}>{post.dis}</span>  </span>
                         <span style={{marginLeft:"120px",fontSize:"25px", color:"red"}}>&#8377;{post.discounted_fees}</span>
                         </div>
                         <div> <span style={{marginBottom:"20px",marginLeft:"410px",fontSize:"11px",color:"gray"}}>{post.fees_cycle}</span></div>
                        

                       <div style={{display:"flex"}}>
                       <span style={{color:"	#00ffff",fontSize:"12px",marginLeft:"15px"}}> 93% match :    </span><span style={{color:"gray",fontSize:"12px"}}>{post.famous_nearest_places}</span>
                       </div>

                       <div>
                           <button style={{color:"white",backgroundColor:"#00ffff",marginRight:"235px",top:"50%", fontSize:"12px"}}>{post.offertext}</button>
                       </div>

                   </div>
                  
               </div>
              </>
            )
          })}
        </div>
    
    );
  }
  
export default Card
